app.factory('AppLang', function($rootScope, $timeout, gettext, gettextCatalog) {
    var
        service = {},
        inited = false,
        siteLang = AppConfig.lang,
        currentLang = AppConfig.current_lang,
        currentLangUrlPrefix = '';

    service.langs = {
        'ru': gettext('RU'),
        'en': gettext('EN')
    };

    var key = null,
        title = null;
    for (var i = 0; i < AppConfig.lang_list.length; i++) {
        key = AppConfig.lang_list[i].code;
        title = AppConfig.lang_list[i].title;
        service.langs[key] = gettext(title);
    }

    service.getUrlPrefix = function() {
        return currentLangUrlPrefix;
    };

    service.getCurrent = function() {
        return currentLang;
    };

    service.setCurrent = function(code) {
        if (code === undefined)
            code = currentLang;

        if (currentLang != code || inited === false) {
            inited = true;
            currentLang = code;
            if (currentLang != siteLang)
                currentLangUrlPrefix = '/' + currentLang;
            else
                currentLangUrlPrefix = '';

            gettextCatalog.debug = true;
            gettextCatalog.setCurrentLanguage(code);

            $timeout(function() {
                $rootScope.$broadcast('lang.changed', code);
            });
        }
    };

    return service;
});
app.factory('AppProperties', function(AppLang) {
    var service = {},
        list = angular.copy(AppConfig.properties);

    service.load = function(newList) {
        list = {};
        if (newList.length)
            for (var i = 0; i < newList.length; i++) {
                list[newList[i].name] = newList[i]['value_' + AppLang.getCurrent()];
            }
    };

    service.update = function(item) {
        list[item.name] = item['value_' + AppLang.getCurrent()];
    };

    service.get = function(name, defValue) {
        if (defValue === undefined)
            defValue = null;
        if (list[name] !== undefined) {
            return list[name];
        } else {
            list[name] = defValue;
        }
        return list[name];
    };

    return service;
});
app.run(function(AppLang, $route, $rootScope, $timeout) {

    $rootScope.$on('$routeChangeStart', function(event, current, previous) {
        $timeout(function() {
            var lang=null;

            if ($route.current !== undefined && $route.current.params !== undefined &&
                $route.current.params.lang !== undefined)
                lang=$route.current.params.lang;
            else
            if ($route.current !== undefined && $route.current.$$route !== undefined &&
                $route.current.$$route.params !== undefined &&
                $route.current.$$route.params.lang !== undefined)
                lang=$route.current.$$route.params.lang;

            if (lang!==null && AppLang.langs[lang]!==undefined)
                AppLang.setCurrent(lang);
            else
                AppLang.setCurrent();
        });
    });

    AppLang.setCurrent();
});
