app.factory('MessageSvc', function (AppConst, $rootScope, $modalBox, $alert) {
    var service={};

    service.list=false;

    var extVSprintF=function(message, data){
        var new_data=[]
        var new_message=message;

        if (Array.isArray(data)){
            for (var key in data){
                if (typeof data[key] !== 'object' && !Array.isArray(data[key]))
                    new_data.push(data[key]);
            }
        }
        else
        if (typeof data === 'object'){
            for (var key in data){
                if (typeof data[key] !== 'object' && !Array.isArray(data[key]))
                    new_message=new_message.replace(new RegExp('%'+key, 'ig'),data[key]);
            }
        }
        else
        if (data!=undefined)
            new_data.push(data);
        return vsprintf(new_message, new_data);
    }

    service.error=function(message, data, callbackOk){
        if (data===undefined)
            data={values:[]};

        if (data.title===undefined)
            data.title='Error';

        if (callbackOk===undefined)
            callbackOk=function(){
            }
        if (service.list[message]!==undefined)
            message=service.list[message];

        var boxOptions = {
            title: data.title,
            content: extVSprintF(message, data.values),
            theme: 'danger',
            effect: false,
            afterOk: callbackOk,
            html: true
        }

        $modalBox(boxOptions);
        $rootScope.$broadcast('message.error', message, data, callbackOk);
    }

    service.alert=function(message, data, callbackOk){
        if (data===undefined)
            data={values:[]};

        if (data.title===undefined)
            data.title='Info';

        if (callbackOk===undefined)
            callbackOk=function(){
            }
        if (service.list[message]!==undefined)
            message=service.list[message];

        var boxOptions = {
            title: data.title,
            content: extVSprintF(message, data.values),
            theme: 'alert',
            effect: false,
            afterOk: callbackOk,
            html: true
        }

        $modalBox(boxOptions);
        $rootScope.$broadcast('message.alert', message, data, callbackOk);
    }

    service.confirm=function(message, data, callbackOk, callbackCancel){
        if (data===undefined)
            data={values:[]};

        if (data.title===undefined)
            data.title='Message';

        if (callbackOk===undefined)
            callbackOk=function(){
            }
        if (callbackCancel===undefined)
            callbackCancel=function(){
            }
        if (service.list[message]!==undefined)
            message=service.list[message];

        var boxOptions = {
            title: data.title,
            content: extVSprintF(message, data.values),
            boxType: 'confirm',
            theme: 'alert',
            effect: false,
            confirmText: 'Yes',
            cancelText: 'No',
            afterConfirm: callbackOk,
            afterCancel: callbackCancel,
            html: true
        }

        $modalBox(boxOptions);
        $rootScope.$broadcast('message.confirm', message, data, callbackOk);
    }


    service.info=function(message, data, type){
        service.alert(message, data);
        /*
        if (data===undefined)
            data={values:[]};

        if (data.title===undefined)
            data.title='';
        if (data.alertType===undefined)
            data.alertType='info';
        if (data.placement===undefined)
            data.placement='center';

        if (service.list[message]!==undefined)
            message=service.list[message];

        //$alert(extVSprintF(message, data.values), data.title, data.alertType, data.placement)
        */
    }

    service.init=function(){
        service.list={};
        for (var key in AppConst){
            if (AppConst[key]['message']!==undefined){
                angular.extend(service.list, AppConst[key]['message']);
            }
        }
    }

    if (service.list===false)
        service.init();

    return service;
  });
app.factory('NavbarSvc', function ($routeParams, $rootScope, $route, $location, $window, AppConst) {
    var service={};

    $rootScope.$on('$routeChangeStart',function(event, current, previous){
        service.init();
        $rootScope.$broadcast('navbar.change', event, current, previous);
    });

    function modifiItem(item){
        var navItem={};

        if (item.parent!=undefined && AppConst[item.parent]!=undefined && AppConst[item.parent][item.name]!=undefined)
            navItem=AppConst[item.parent][item.name];
        if (AppConst[item.name]!=undefined)
            navItem=AppConst[item.name];

        if (navItem.title!=undefined)
            item.title=navItem.title;

        if (navItem.url!=undefined)
            item.url=navItem.url;

        if (navItem.strings!=undefined && navItem.strings.title!=undefined)
            item.title=navItem.strings.title;

        if (navItem.urls!=undefined && navItem.urls.url!=undefined)
            item.url=navItem.urls.url;

        if (item.url===undefined)
            item.url='#/'+item.name;

        item.active=(item.name==$routeParams.navId);
        if (item.hiddenHandler!=undefined)
            item.hidden=item.hiddenHandler();
        else
            if (item.hidden===undefined)
                item.hidden=false;
    }

    service.goBack=function(){
        console.log($window.history.length);
        if ($window.history.length>2)
            $window.history.back();
        else
            service.goHome();
    }
    service.goHome=function(){
        $location.path(service.homeUrl);
    }

    service.init=function(navId){
        if (navId!=undefined)
            $routeParams.navId=navId;
        else
        if ($route.current !== undefined && $route.current.$$route!==undefined && $route.current.$$route.navId!=undefined)
            $routeParams.navId=$route.current.$$route.navId
        else
        if ($route.current !== undefined && $route.current.params!==undefined && $route.current.params.navId!=undefined)
            $routeParams.navId=$route.current.params.navId;

        service.homeUrl=AppConst.homeUrl;
        service.brand=AppConst.brand;
        service.items=AppConst.navbar;
        for (var i=0;i<service.items.left.length;i++){
            modifiItem(service.items.left[i]);
        }
        for (var i=0;i<service.items.right.length;i++){
            modifiItem(service.items.right[i]);
        }
        $rootScope.$broadcast('navbar.change', false, {current:{params:{navId:$routeParams.navId}}}, false);
    }

    return service;
  });
